import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-counter-component',
  templateUrl: './counter.component.html'
})
//export class CounterComponent {
//  public currentCount = 0;

//  public incrementCounter() {
//    this.currentCount++;
//  }
//}

export class RegistrationFormComponent {
  registrationForm: FormGroup;

  constructor(private formBuilder: FormBuilder) {
    this.registrationForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(8)]],
      confirmPassword: ['', Validators.required],
      phoneNumber: ['', Validators.required],
      address: [''],
      city: [''],
      state: [''],
      zipCode: ['']
    });
  }

  onSubmit() {
    if (this.registrationForm.valid) {
      // Handle form submission, e.g., send data to the server
      console.log(this.registrationForm.value);
    } else {
      // Form is invalid, show error messages
      // You can access form controls like this: this.registrationForm.get('controlName')
    }
  }
}
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8" />
  <title></title>
</head>
<body>
  <!-- registration-form.component.html -->

  <form [formGroup]="registrationForm" (ngSubmit)="onSubmit()" class="registration-form">
    <div class="form-group">
      <label for="firstName">First Name</label>
      <input type="text" id="firstName" formControlName="firstName" placeholder="Enter your first name">
    </div>

    <div class="form-group">
      <label for="lastName">Last Name</label>
      <input type="text" id="lastName" formControlName="lastName" placeholder="Enter your last name">
    </div>

    <div class="form-group">
      <label for="email">Email</label>
      <input type="email" id="email" formControlName="email" placeholder="Enter your email">
    </div>

    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" id="password" formControlName="password" placeholder="Enter a password">
    </div>

    <div class="form-group">
      <label for="confirmPassword">Confirm Password</label>
      <input type="password" id="confirmPassword" formControlName="confirmPassword" placeholder="Confirm your password">
    </div>

    <div class="form-group">
      <label for="phoneNumber">Phone Number</label>
      <input type="tel" id="phoneNumber" formControlName="phoneNumber" placeholder="Enter your phone number">
    </div>

    <div class="form-group">
      <label for="address">Address</label>
      <input type="text" id="address" formControlName="address" placeholder="Enter your address">
    </div>

    <div class="form-group">
      <label for="city">City</label>
      <input type="text" id="city" formControlName="city" placeholder="Enter your city">
    </div>

    <div class="form-group">
      <label for="state">State</label>
      <input type="text" id="state" formControlName="state" placeholder="Enter your state">
    </div>

    <div class="form-group">
      <label for="zipCode">ZIP Code</label>
      <input type="text" id="zipCode" formControlName="zipCode" placeholder="Enter your ZIP code">
    </div>

    <div class="form-group">
      <button type="submit" [disabled]="!registrationForm.valid">Register</button>
    </div>
  </form>

</body>
</html>
